import React from 'react';

export default class DisplayList extends React.Component {
    constructor(props){
        super(props);
        sort: props.sorted
    }

    sortArray = (array,key) =>{
        this.setState({sorted: true});
        return array.sort((a, b)=>{
            return a[key] - b[key];

        });
    };

    render(){
        return(
            <div>
                <Button onClick={()=> this.sortArray(item,props.key)} variant = "outline-primary" >sort</Button>{' '} 
                {this.props.list.map((item) => 
                {return <li>{item.name}, {item.topic}, {item.year}</li>}
                )}

            </div>


        )
    }
}
