import React from 'react';
import Button from '@material-ui/core/Button';
import DisplayList from './DisplayList';

export default class FilteredList extends React.Component {
    constructor(props){
        super(props);
            this.state = {
                topic: "All"
            };
    }

    onSelectFilterTopic = (event) => {
        console.log(event)
        this.setState({
            topic: event
        })

    };

    matchesFilterTopic = (item) => {
        if (this.state.topic ==="All"){
            return true
        } else if (this.state.topic === item.topic){
            return true
        } else {
            return false
        }
    }

    render(){
        return(
            <div>
                <Button eventKey = "All" onClick={(event) => this.onSelectFilterTopic(event) } variant = "outlined" color= "primary">All</Button>
                <Button eventKey = "Design" onClick={(event) => { this.onSelectFilterTopic(event) }} variant = "outlined" color= "Design">Design</Button>
                <Button eventKey = "Psychology" onClick={(event) => { this.onSelectFilterTopic }} variant = "outlined" color= "Psychology">Psychology</Button>
                <Button eventKey = "Biology" onClick={() => { this.onSelectFilterTopic }} variant = "outlined" color= "Biology">Biology</Button>
                <DisplayList list={this.props.list.filter(this.matchesFilterTopic)}/>
            </div>

        )
    }
}
