import React from 'react';
import Button from 'react-bootstrap/Button';
import AggregatedList from './AggregatedList';

var aggregatedList = [];

export default class DisplayList extends React.Component {
    constructor(props){
        super(props);
    }

    addtoAggregator =(arr,item) =>{
        arr.push(item);
        console.log(arr);
    }

    removefromAggregator= (arr, item)=>{
        return arr.filter(function(ele){
            console.log(ele !==item);
            return ele !==item;

        });
    }

    render(){
        return(
            <div class = "columns">
                <div class = "card-columns">
                    {this.props.list.map((item) => 
                    <div class = "card" style = {{width: "400px"}}>
                        {console.log(item.name)}
                        <img class="card-img-top" alt="Card image cap" src = {item.image}/> 
                        <div class="card-body">
                            <h5 class="card-title" align= "center" >{item.name}</h5>
                        </div>
                        <ul class="list-group list-group-flush">
                            <li class="list-group-item" align= "center">{item.topic}, {item.year}</li>
                        </ul>
                        <div class = "card-body" align= "center">
                        <Button onClick={()=> this.addtoAggregator(aggregatedList, item) }  variant = "outline-primary" >Add</Button>{' '}
                        <Button onClick={(arr)=> {arr = this.removefromAggregator(aggregatedList, item)} }  variant = "outline-primary" >Remove</Button>{' '}
                        </div> 

                    </div>

                    )}

                </div>
                <AggregatedList list = {aggregatedList}/>
            </div>

        )
    }
}
