import React from 'react';
import './App.css';
import FilteredList from './FilteredList'
import Button from 'react-bootstrap/Button';

 const productList= Array.from([

  { name: "Nasa", topic: "biology", year: "2020", order: "a"},
  { name: "Metal", topic: "design", year: "2019", order: "k"},
  { name: "Coralogy", topic: "psychology", year: "2019", order: "g"},
  { name: "Tilly", topic: "biology", year: "2019", order: "f"},
  { name: "Block", topic: "design", year: "2018", order: "j"},
  { name: "Connections", topic: "design", year: "2019", order: "l"},
  { name: "Coffee", topic: "design", year: "2020", order: "b"},
  { name: "Mouse", topic: "design", year: "2020", order: "i"},
  { name: "Responsive", topic: "psychology", year: "2020", order: "e"},
  { name: "Iterative", topic: "design", year: "2020", order: "c"},
  { name: "AB", topic: "design", year: "2020", order: "d"},
  { name: "Personas", topic: "psychology", year: "2020", order: "h"},
  ]);

  console.log(productList.hasOwnProperty(productList.order));
  var sortedList = productList.sort(productList.year);
  console.log(sortedList);
  console.log(productList);

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      sort: props.sorted
    };
  }

  setSorted() {
    this.setState({sorted: true});
  }




  render() {
    return (
      <div>
        <Button onClick={()=> productList = this.setSorted()} variant = "outline-primary" >Sort</Button>{' '} 
        { this.state.sorted ? (

          <FilteredList list = {sortedList}/>

        ): (
          <FilteredList list={productList} />
        ) }

	    </div>

    )
  }
}
