import React from 'react';
import {Button} from '@material-ui/core';
import {ButtonGroup} from '@material-ui/core';
import {RadioGroup} from '@material-ui/core';
import DisplayList from './DisplayList';


export default class FilteredList extends React.Component {
    constructor(props){
        super(props);
            this.state = {
                topic: "All",
                year: "All",
                //makes button bold when toggled
                buttonvariant1: "outlined",
                buttonvariant2: "outlined",
                buttonvariant3: "outlined",
                buttonvariant4: "outlined"

            };

    }

    onSelectFilterTopic = (event) => {
        this.setState({
            topic: event,
        })
        if (this.state.topic ==="All" && this.state.buttonvariant1 ==="outlined"){
            this.setState({buttonvariant1: "contained"})
        } else if (this.state.topic ==="All" && this.state.buttonvariant1 ==="contained"){
            this.setState({buttonvariant1: "outlined"})}

        if (this.state.topic ==="All" && this.state.buttonvariant2 ==="outlined"){
            this.setState({buttonvariant2: "contained"})
        } else if (this.state.topic ==="All" && this.state.buttonvariant2 ==="contained"){
            this.setState({buttonvariant2: "outlined"})
        }


    };

    matchesFilterItems = (item) => {
        if (this.state.topic ==="All" && this.state.year ==="All") {
            return true
        } else if (this.state.topic === item.topic && this.state.year ==="All"){
            return true
        } else if (this.state.topic === "All" && this.state.year ===item.year){
            return true  
        } else if (this.state.topic === item.topic && this.state.year ===item.year){
            return true
        } else {
            return false
        }

    }

    onSelectFilterYear = (event) => {
        this.setState({
            year: event

        })

    };



    render(){
        return(

            <div>
                <RadioGroup row>
                <Button onClick={()=> this.onSelectFilterTopic("All")} variant = {this.state.buttonvariant1} color = "primary" size= "large">All</Button>{' '}
                <Button onClick={()=> this.onSelectFilterTopic("design")} variant = {this.state.buttonvariant2}  color = "primary" size= "large">Design</Button>{' '}
                <Button onClick={()=> this.onSelectFilterTopic("psychology")} variant = {this.state.buttonvariant3}  color = "primary" size= "large">Psychology</Button>{' '}
                <Button onClick={()=> this.onSelectFilterTopic("biology")} variant = {this.state.buttonvariant4}  color = "primary"size= "large">Biology</Button>{' '}
                </RadioGroup >
                <br></br>
                <br></br>
                <Button onClick={()=> this.onSelectFilterYear("All")} variant = "outline-primary" >All</Button>{' '}
                <Button onClick={()=> this.onSelectFilterYear("2018")} variant = "outline-primary" >2018</Button>{''}
                <Button onClick={()=> this.onSelectFilterYear("2019")} variant = "outline-primary" >2019</Button>{' '}
                <Button onClick={()=> this.onSelectFilterYear("2020")} variant = "outline-primary" >2020</Button>{' '} 
                <DisplayList list={this.props.list.filter(this.matchesFilterItems)} />


            </div>


        )
    }

}
