import React from 'react';
import './App.css';
import FilteredList from './FilteredList'
import Nasa from './nasa.gif';
import Metal from './metal.jpg';
import Coralogy from './coralogy.jpg';
import Tilly from './tilly.jpg';
import Block from './block.jpg';
import Connections from './connections.jpg';
import Coffee from './coffee.jpg';
import Mouse from './mouse.jpg';
import Responsive from './responsive.jpg';
import Iterative from './iterative.jpg';
import Ab from './ab.jpg';
import Personas from './personas.png';
import {Button} from '@material-ui/core';
import EcoIcon from '@material-ui/icons/Eco';
import LocalFloristIcon from '@material-ui/icons/LocalFlorist';
import ColorLensIcon from '@material-ui/icons/ColorLens';
import SpaIcon from '@material-ui/icons/Spa';
import FaceIcon from '@material-ui/icons/Face';


 var productList= [

  { name: "Nasa", topic: "biology", year: "2020", order: 1, image: Nasa, icon: <EcoIcon/>},
  { name: "Metal", topic: "design", year: "2019", order: 11, image: Metal, icon: <ColorLensIcon/> },
  { name: "Coralogy", topic: "psychology", year: "2019", order:10, image: Coralogy, icon: <FaceIcon/>},
  { name: "Tilly", topic: "biology", year: "2019", order: 9, image: Tilly, icon: <EcoIcon/>},
  { name: "Block", topic: "design", year: "2018", order: 12, image: Block, icon: <ColorLensIcon/> },
  { name: "Connections", topic: "design", year: "2019", order: 8, image: Connections,icon: <ColorLensIcon/>},
  { name: "Coffee", topic: "design", year: "2020", order: 2, image: Coffee, icon: <ColorLensIcon/>},
  { name: "Mouse", topic: "design", year: "2020", order: 7, image: Mouse, icon: <ColorLensIcon/>},
  { name: "Responsive", topic: "psychology", year: "2020", order: 5, image: Responsive,icon: <FaceIcon/>},
  { name: "Iterative", topic: "design", year: "2020", order: 3, image: Iterative,icon: <ColorLensIcon/>},
  { name: "AB", topic: "design", year: "2020", order: 4, image: Ab, icon: <ColorLensIcon/>},
  { name: "Personas", topic: "psychology", year: "2020", order: 6, image: Personas, icon: <FaceIcon/>},
  ];


console.log(productList);

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      sort: props.sorted
    };
  }

  setSorted() {
    this.setState({sorted: true});
  }




  render() {
    return (
      <div class = "App">
        <Button onClick={()=> this.setSorted()} variant = "outline-primary" >Sort</Button>{' '} 
        { this.state.sorted ? (

          <FilteredList list = {

            productList.sort(function (a, b) {
              return a.order- b.order;
            })
          }/>

        ): (
          <FilteredList list={productList} />
        ) }

	    </div>

    )
  }
}
