import React from 'react';
import './App.css';
import FilteredList from './FilteredList'
import Button from 'react-bootstrap/Button';

 var productList= [

  { name: "Nasa", topic: "biology", year: "2020", order: 1, image: "./nasa.gif"},
  { name: "Metal", topic: "design", year: "2019", order: 11, image:"./metal.jpg"},
  { name: "Coralogy", topic: "psychology", year: "2019", order:10, image:"./coralogy.jpg"},
  { name: "Tilly", topic: "biology", year: "2019", order: 9, image:"./tilly.jpg"},
  { name: "Block", topic: "design", year: "2018", order: 12, image:"./block.jpg"},
  { name: "Connections", topic: "design", year: "2019", order: 8, image:"./connections.jpg"},
  { name: "Coffee", topic: "design", year: "2020", order: 2, image:"./coffee.jpg"},
  { name: "Mouse", topic: "design", year: "2020", order: 7, image:"mouse.jpg"},
  { name: "Responsive", topic: "psychology", year: "2020", order: 5, image:"responsive.jpg"},
  { name: "Iterative", topic: "design", year: "2020", order: 3, image:"iterative.jpg"},
  { name: "AB", topic: "design", year: "2020", order: 4, image:"ab.jpg"},
  { name: "Personas", topic: "psychology", year: "2020", order: 6, image:"personas.png"},
  ];


console.log(productList);

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      sort: props.sorted
    };
  }

  setSorted() {
    this.setState({sorted: true});
  }




  render() {
    return (
      <div>
        <Button onClick={()=> this.setSorted()} variant = "outline-primary" >Sort</Button>{' '} 
        { this.state.sorted ? (

          <FilteredList list = {

            productList.sort(function (a, b) {
              return a.order- b.order;
            })
          }/>

        ): (
          <FilteredList list={productList} />
        ) }

	    </div>

    )
  }
}
