//import {Button} from '@material-ui/core';
import DisplayList from './DisplayList';

export default class FilteredList extends React.Component {
    constructor(props){
        super(props);
            this.state = {
                topic: "All"
            };
    }

    onSelectFilterTopic = (event) => {
        console.log(event)
        this.setState({
            topic: event
        })

    };

    matchesFilterTopic = (item) => {
        if (this.state.topic ==="All"){
            return true
        } else if (this.state.topic === item.topic){
            return true
        } else {
            return false
        }
    }

    render(){
        return(
            <div>
                {/* <Button eventKey = "All" onClick={()=> console.log("hello")} variant = "outlined" color= "primary">All</Button>
