import React from 'react';
import Button from 'react-bootstrap/Button';



export default class AggregatedList extends React.Component {
    constructor(props){
        super(props);

    }



 render(){
     return(
         <div>
        <div class = "card-columns">
                    {this.props.list.map((item) => 
                    <div class = "card" style = {{width: "400px"}}>
                        {console.log(item.name)}
                        <img class="card-img-top" alt="Card image cap" src = {item.image}/> 
                        <div class="card-body">
                            <h5 class="card-title" align= "center" >{item.name}</h5>
                        </div>
                        <ul class="list-group list-group-flush">
                            <li class="list-group-item" align= "center">{item.topic}, {item.year}</li>
                        </ul>
                        <div class = "card-body" align= "center">
                        <Button onClick={()=> this.addtoAggregator(this.props.list, item) }  variant = "outline-primary" >Add</Button>{' '}
                        <Button onClick={(arr)=> {arr = this.removefromAggregator(this.props.list, item)} }  variant = "outline-primary" >Remove</Button>{' '}
                        </div> 

                    </div>

                    )}

                </div>
        </div>
     )
 }
}
