import React from 'react';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import DisplayList from './DisplayList';

const sortArray= [];

export default class FilteredList extends React.Component {
    constructor(props){
        super(props);
            this.state = {
                topic: "All",
                year: "All"
            };
    }

    onSelectFilterTopic = (event) => {
        this.setState({
            topic: event
        })

    };



    matchesFilterItems = (item) => {
        if (this.state.topic ==="All" && this.state.year ==="All") {
            return true
        } else if (this.state.topic === item.topic && this.state.year ==="All"){
            return true
        } else if (this.state.topic === "All" && this.state.year ===item.year){
            return true  
        } else if (this.state.topic === item.topic && this.state.year ===item.year){
            return true
        } else {
            return false
        }

    }

    onSelectFilterYear = (event) => {
        this.setState({
            year: event
        })

    };

    onSelectSortOrder = (event) => {
        this.setState({
            order: event
        })

    };

    sortItems = (item) => {
        this.props.list.filter(this.matchesFilterItems);
        console.log(item);

    }






    render(){
        return(


            <div>

                <Button value = "All" onClick={()=> this.onSelectFilterTopic("All")} type= "checkbox" class="btn btn-primary" aria-pressed="false" variant = "primary" color = "primary" >All</Button>{' '}
                <Button value = "design" onClick={()=> this.onSelectFilterTopic("design")} variant = "primary" >Design</Button>{' '}
                <Button value = "psychology" onClick={()=> this.onSelectFilterTopic("psychology")} variant = "primary" color= "Psychology">Psychology</Button>{' '}
                <Button value = "biology" onClick={()=> this.onSelectFilterTopic("biology")} variant = "primary" color= "Biology">Biology</Button>{' '}
                <br></br>
                <br></br>
                <Button value = "All" onClick={()=> this.onSelectFilterYear("All")} variant = "outline-primary" >All</Button>{' '}
                <Button value = "2018" onClick={()=> this.onSelectFilterYear("2018")} variant = "outline-primary" >2018</Button>{''}
                <Button value = "2019" onClick={()=> this.onSelectFilterYear("2019")} variant = "outline-primary" >2019</Button>{' '}
                <Button value = "2020" onClick={()=> this.onSelectFilterYear("2020")} variant = "outline-primary" >2020</Button>{' '} 
                <Button value = "Sort" onClick={()=> this.props.list.sort(this.state.topic)} variant = "outline-primary" > Sort </Button>{' '} 
                <DisplayList list={this.props.list.filter(this.matchesFilterItems)} />



            </div>


        )
    }

}
