import React from 'react';
import ToggleButton from 'react-bootstrap/ToggleButton';
import DisplayList from './DisplayList';

export default class FilteredList extends React.Component {
    constructor(props){
        super(props);
            this.state = {
                topic: "All",
                year: "All"
            };
    }

    onSelectFilterTopic = (event) => {
        this.setState({
            topic: event
        })

    };

    matchesFilterItems = (item) => {
        if (this.state.topic ==="All" && this.state.year ==="All") {
            return true
        } else if (this.state.topic === item.topic && this.state.year ==="All"){
            return true
        } else if (this.state.topic === "All" && this.state.year ===item.year){
            return true  
        } else if (this.state.topic === item.topic && this.state.year ===item.year){
            return true
        } else {
            return false
        }

    }

    onSelectFilterYear = (event) => {
        this.setState({
            year: event
        })

    };





    render(){
        return(
            <div>
                <ToggleButton onClick={()=> this.onSelectFilterTopic("All")} variant = "primary" color = "primary" >All</ToggleButton>{' '}
                <ToggleButton value = "design" onClick={()=> this.onSelectFilterTopic("design")} variant = "primary" >Design</ToggleButton>{' '}
                <ToggleButton value = "psychology" onClick={()=> this.onSelectFilterTopic("psychology")} variant = "primary" color= "Psychology">Psychology</ToggleButton>{' '}
                <ToggleButton value = "biology" onClick={()=> this.onSelectFilterTopic("biology")} variant = "primary" color= "Biology">Biology</ToggleButton>{' '}
                <br></br>
                <br></br>
                <ToggleButton value = "All" onClick={()=> this.onSelectFilterYear("All")} variant = "outline-primary" color = "primary">All</ToggleButton>{' '}
                <ToggleButton value = "2018" onClick={()=> this.onSelectFilterYear("2018")} variant = "outline-design" >2018</ToggleButton>{''}
                <ToggleButton value = "2019" onClick={()=> this.onSelectFilterYear("2019")} variant = "outlined" color= "Psychology">2019</ToggleButton>{' '}
                <ToggleButton value = "2020" onClick={()=> this.onSelectFilterYear("2020")} variant = "outlined" color= "Biology">2020</ToggleButton>{' '} 
                <DisplayList list={this.props.list.filter(this.matchesFilterItems)} />

            </div>


        )
    }
}
