import React from 'react';
import {Button} from '@material-ui/core';
import AggregatedList from './AggregatedList';
import './DisplayList.css';
import {Card} from '@material-ui/core';
import ColorLensIcon from '@material-ui/icons/ColorLens';
import SpaIcon from '@material-ui/icons/Spa';
import FaceIcon from '@material-ui/icons/Face';
import { TramOutlined } from '@material-ui/icons';

export default class DisplayList extends React.Component {
    constructor(props){
        super(props);
        this.addtoAggregator = this.addtoAggregator.bind(this);
        this.removefromAggregator = this.removefromAggregator.bind(this);
        this.state= {
            aggregatedList:[],
            topicList:[],
            click: props.clicked,
            popup: "Add 2 or more items to see the connections!"
        }
    }

    componentDidUpdate(props){
        console.log(this.state.aggregatedList);

    }

    //method that adds item to the aggregatedList
    addtoAggregator = (item) => {
        this.setState({aggregatedList:[...this.state.aggregatedList, item]})
        this.setState({topicList:[...this.state.aggregatedList, item]})
        this.setState({clicked: false})
    }

    //method that removes item from the aggregatedList


    removefromAggregator = (item) =>{
        this.setState({aggregatedList: this.state.aggregatedList.filter(function(ele){
            return ele !== item;
        })});
        this.setState({topicList: this.state.topicList.filter(function(ele){
            return ele !== item;
        })});
        this.setState({clicked: false})

    }
    //checks if the topicList includes the topic that is passed in, and determines the text that pops up with each combo of topics
    checkTopic = () =>{
        if (this.state.topicList.includes("biology", "psychology")=== true){
            this.setState({popup: "Psych+Bio"})
        }else if (this.state.topicList.includes("biology", "design")=== true){
            this.setState({popup: "bio+design"})
        }else if (this.state.topicList.includes("psychology", "design")=== true){
            this.setState({popup: "psych+design"})
        }
    }

    setClicked(){
        this.setState({clicked: true});
    }

    render(){
        return(
            <div class = "rows">
                <Card style = {{width: "900px"}}>
                    <div class = "gridwrap">
                        {this.props.list.map((item) => 
                        <div class = "cardwrap">
                        <Card style = {{width: "200px"}} >
                            <img class="card-img-top" src = {item.image}/> 
                            <div class="card-body">
                                <h5 class="card-title" align= "center" >{item.name}</h5>
                            </div>
                            <ul class="list-group list-group-flush">
                                <li class="list-group-item" align= "center">{item.topic}, {item.year}</li>
                            </ul>
                            <div class = "card-body" align= "center">
                            <Button onClick={()=> this.addtoAggregator(item) }  variant = "outline-primary" >Add</Button>{' '}
                            </div> 
                        </Card>
                        </div>
                        )}
                    </div>
                </Card>


                {/* <AggregatedList newlist = {this.state.aggregatedList} addtoAggregator ={this.addtoAggregator} removefromAggregator = {this.removefromAggregator}/> */}
                <Card class = "bigcard" style = {{width: 500}}>
                    <div class = "gridwrap">

                        {this.state.aggregatedList.map((item) => 
                        <div class = "cardwrap">
                        <Card style = {{width: "200px"}}>
                            <img class="card-img-top" src = {item.image}/> 
                            <h5 class="card-title" align= "center" >{item.name}</h5>
                            <div class = "card-body" align= "center">
                            <Button onClick={()=> this.addtoAggregator(item) }  variant = "outline-primary" >Add</Button>{' '}
                            <Button onClick={()=> this.removefromAggregator(item) }  variant = "outline-primary" >Remove</Button>{' '}
                            </div> 

                        </Card>
                        </div>
                        )}
                    </div>

                    <Card>

                        {this.state.aggregatedList.map((item)=>

                        {if (item.topic === "biology"){
                            return <SpaIcon/>
                        } else if (item.topic === "psychology"){
                            return <FaceIcon/>
                        } else if (item.topic === "design"){
                            return <ColorLensIcon/>
                        } else {
                        }

                        })}
                        <Button onClick={()=> this.checkTopic()}  variant = "outline-primary" >Look for Connections!</Button>{' '}
                    </Card>
                    <Card>
                    <h5 class="card-title" align= "center" >{this.state.popup}</h5>
                    </Card>
                </Card>
            </div>

        )
    }
}
