import React from 'react';
import './App.css';
import FilteredList from './FilteredList'
import Button from 'react-bootstrap/Button';

var productList = [

  { name: "Nasa", topic: "biology", year: "2020", order: 1},
  { name: "Metal", topic: "design", year: "2019", order: 11},
  { name: "Coralogy", topic: "psychology", year: "2019", order: 8},
  { name: "Tilly", topic: "biology", year: "2019", order: 7},
  { name: "Block", topic: "design", year: "2018", order: 12},
  { name: "Connections", topic: "design", year: "2019", order: 9},
  { name: "Coffee", topic: "design", year: "2020", order: 2},
  { name: "Mouse", topic: "design", year: "2020", order: 10},
  { name: "Responsive", topic: "psychology", year: "2020", order: 5},
  { name: "Iterative", topic: "design", year: "2020", order: 3},
  { name: "AB", topic: "design", year: "2020", order: 4},
  { name: "Personas", topic: "psychology", year: "2020", order: 6},
  ];

var sortedList = this.sortArray(productList, productList.order);
console.log(sortedList);

export default class App extends React.Component {
  constructor(props){
    super(props);
  }


  sortArray = (array,key) =>{
    this.setState({sorted: true});
    return array.sort((a, b)=>{
        return a[key] - b[key];

    });
  };


  render() {
    return (
      <div>
		    <FilteredList list={productList} />
	    </div>

    )
  }
}
