import React from 'react';
import Button from 'react-bootstrap/Button';



export default class DisplayList extends React.Component {
    constructor(props){
        super(props);
        this.state= {
            aggregatorList: [],
        };
        {console.log(aggregatorList)}
    }

    addtoAggregator =(item) =>{
        this.setState({aggregatorList:[...this.state.aggregatorList, item]});
    }

    render(){
        return(
            <div class = "card-columns">
                {this.props.list.map((item) => 
                   <div class = "card" style = {{width: "400px"}}>
                       {console.log(item.name)}
                     <img class="card-img-top" alt="Card image cap" src = {item.image}/> 
                    <div class="card-body">
                        <h5 class="card-title">{item.name}</h5>
                        <p class="card-text">card text</p>
                     </div>
                     <ul class="list-group list-group-flush">
                        <li class="list-group-item">{item.topic}</li>
                        <li class="list-group-item">{item.year}</li>
                     </ul>
                     <Button onClick={()=> this.addtoAggregator(item) } variant = "outline-primary" >Add</Button>{' '}


                   </div>

                )}

            </div>


        )
    }
}
